INSERT INTO `news` (`id`, `title`, `titlekd`, `img`, `content`, `sum`, `created_at`, `updated_at`) VALUES
(1, 'Chính sách vận chuyển hàng', 'chinh-sach-van-chuyen-hang', 'news1.jpg', '<p><br></p><p><strong><span style="font-family: arial,helvetica,sans-serif; font-size: medium;">1/ Giao hàng miễn phí</span></strong></p><p>\r\n<br></p><p><span style="font-family: arial,helvetica,sans-serif; font-size: medium;">1.1/ Giao hàng miễn phí 20 km áp dụng với đơn hàng từ 500.000 vnđ trở lên.</span></p><p>\r\n<br></p><p><span style="font-family: arial,helvetica,sans-serif; font-size: medium;">1.2/ Thời gian đáp ứng yêu cầu:</span></p><p>\r\n<br></p><ul><li><span style="font-family: arial,helvetica,sans-serif; font-size: medium;"><span lang="VI">T</span>rong 3h dưới 25km</span></li><li><span style="font-family: arial,helvetica,sans-serif; font-size: medium;"><span lang="VI">T</span>rong 4h với khoảng cách <span style="color: #000000;">từ 25 đến 35km</span></span></li><li><span style="font-family: arial,helvetica,sans-serif; font-size: medium;"><span lang="VI">Trong 24h với khoảng cách t</span>ừ 35 đến 45km</span></li></ul><p>\r\n<br></p><p style="background: white none repeat scroll 0% 0%;"><span style="font-family: arial,helvetica,sans-serif; font-size: medium;"><strong><span style="color: #222222;" lang="VI">2/ Giao hàng tính phí</span></strong></span></p><p>\r\n<br></p><p><span style="font-family: arial,helvetica,sans-serif; font-size: medium;"><span style="color: #222222;">  Mức thu phí quá km </span><span style="color: #222222;" lang="VI">áp dụng </span><span style="color: #222222;">với xe máy 6000đ</span><span style="color: #222222;" lang="VI">/ km</span><span style="color: #222222;">, </span><span style="color: #222222;" lang="VI">Áp dụng </span><span style="color: #222222;">Ô tô 10.000đ</span><span style="color: #222222;" lang="VI">/ km.</span></span></p><p>\r\n<br></p><p><span style="font-family: arial,helvetica,sans-serif; font-size: medium;"><strong> 2.1/  Áp dụng cho khách lẻ và khách hàng doanh nghiệp</strong></span></p><p>\r\n<br></p><table style="width: 764px; height: 367px;" border="1">\r\n<tbody>\r\n<tr>\r\n<td><span style="font-family: arial,helvetica,sans-serif; font-size: medium;"><strong>Giá Trị Đơn Hàng</strong></span></td>\r\n<td><span style="font-family: arial,helvetica,sans-serif; font-size: medium;"><strong>Số Km được Miễn Phí</strong></span></td>\r\n<td><span style="font-family: arial,helvetica,sans-serif; font-size: medium;"><strong>Thời gian đáp ứng</strong></span></td>\r\n<td><span style="font-family: arial,helvetica,sans-serif; font-size: medium;"><strong>Thu phí</strong></span></td>\r\n</tr>\r\n<tr>\r\n<td><span style="font-family: arial,helvetica,sans-serif; font-size: medium;"><span style="line-height: 115%; color: black;">200.000 đến 500.000 đ</span></span></td>\r\n<td> </td>\r\n<td><span style="font-family: arial,helvetica,sans-serif; font-size: medium;">Giao hàng trong vòng 3h</span></td>\r\n<td><span style="font-family: arial,helvetica,sans-serif; font-size: medium;"><span style="line-height: 115%; color: black;">Giao hàng bán kính 15km và thu phí 20.000đ /1 lần giao. Km16 tính 6000đ/km và tối đa 45 km</span></span></td>\r\n</tr>\r\n<tr>\r\n<td><span style="font-family: arial,helvetica,sans-serif; font-size: medium;"><span style="line-height: 115%; color: black;">500.000 </span><span style="line-height: 115%; color: black;"> </span><span style="line-height: 115%; color: black;">đến 1.000.000 đ</span></span></td>\r\n<td><span style="font-family: arial,helvetica,sans-serif; font-size: medium;">20 Km</span></td>\r\n<td><span style="font-family: arial,helvetica,sans-serif; font-size: medium;"><span style="font-family: arial,helvetica,sans-serif; font-size: medium;">Giao hàng trong vòng</span> 3h</span></td>\r\n<td><span style="font-family: arial,helvetica,sans-serif; font-size: medium;"><span style="line-height: 115%; color: black;">Từ km thứ 21 thu phí 6.000/km, giao hàng tối đa 45 km</span></span></td>\r\n</tr>\r\n<tr>\r\n<td><span style="font-family: arial,helvetica,sans-serif; font-size: medium;"><span style="line-height: 115%; color: black;">>1.000.000 đến 3.000.000 đ</span></span></td>\r\n<td><span style="font-family: arial,helvetica,sans-serif; font-size: medium;"><span style="line-height: 115%; color: black;">25km</span></span></td>\r\n<td><span style="font-family: arial,helvetica,sans-serif; font-size: medium;"><span style="font-family: arial,helvetica,sans-serif; font-size: medium;">Giao hàng trong vòng</span> 3h</span></td>\r\n<td><span style="font-family: arial,helvetica,sans-serif; font-size: medium;"><span style="line-height: 115%; color: black;">Từ km thứ 26 thu phí 6.000/km, giao hàng tối đa 45 km</span></span></td>\r\n</tr>\r\n<tr>\r\n<td><span style="font-family: arial,helvetica,sans-serif; font-size: medium;"><span style="line-height: 115%; color: black;">> 3.000.000 đ</span></span></td>\r\n<td><span style="font-family: arial,helvetica,sans-serif; font-size: medium;"><span style="line-height: 115%; color: black;">35km</span></span></td>\r\n<td><span style="font-family: arial,helvetica,sans-serif; font-size: medium;"><span style="font-family: arial,helvetica,sans-serif; font-size: medium;">Giao hàng trong vòng</span> 4h</span></td>\r\n<td><span style="font-family: arial,helvetica,sans-serif; font-size: medium;"><span style="line-height: 115%; color: black;">Từ km thứ 36 thu phí 6.000/km, giao hàng tối đa 45 km</span></span></td>\r\n</tr>\r\n<tr>\r\n<td colspan="4"><span style="font-family: arial,helvetica,sans-serif; font-size: medium;"></span>\r\n<div style="mso-element: frame; mso-element-frame-hspace: 9.0pt; mso-element-wrap: around; mso-element-anchor-vertical: paragraph; mso-element-anchor-horizontal: margin; mso-element-left: center; mso-element-top: -3.65pt; mso-height-rule: exactly;"><span style="font-family: arial,helvetica,sans-serif; font-size: medium;">- Từ 36 Km đến 45 Km đáp ứng tối đa trong vòng 24h <br></span></div>\r\n<p><span style="font-family: arial,helvetica,sans-serif; font-size: medium;"><span style="line-height: 115%; color: black;">-\r\n Đối với khách hàng vượt quá khoảng cách quy định từ 46km trở lên, áp \r\ndụng phương thức gửi xe khách ( khách hàng chọn xe để gửi ) hoặc chuyển \r\nphát nhanh sau khi đã chuyển khoản, khách hàng thanh toán phí vận chuyển\r\n cho bên giao hàng.</span></span></p>\r\n<p><span style="font-family: arial,helvetica,sans-serif; font-size: medium;"><span style="line-height: 115%; color: black;">-\r\n Đối với đơn hàng từ 500.000 đ đến 3000.000 đ mà cồng kềnh cần phải giao\r\n bằng ô tô thì miễn phí km như khoảng cách giao hàng xe máy và tính phí \r\nvượt quá km đối với ô tô là 10.000/km</span></span></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table><p>\r\n<br></p><p><span style="font-family: arial,helvetica,sans-serif; font-size: medium;"> <strong>2.2/ Chính sách Game Net / Đơn hàng số lượng</strong></span></p><p>\r\n<br></p><table style="width: 764px; height: 276px;" border="1">\r\n<tbody>\r\n<tr>\r\n<td><span style="font-family: arial,helvetica,sans-serif; font-size: medium;"><strong>  Giá Trị Đơn Hàng</strong></span></td>\r\n<td><span style="font-family: arial,helvetica,sans-serif; font-size: medium;"><strong>Số Km được Miễn Phí</strong></span></td>\r\n<td><span style="font-family: arial,helvetica,sans-serif; font-size: medium;"><strong>Thời gian đáp ứng</strong></span></td>\r\n<td><span style="font-family: arial,helvetica,sans-serif; font-size: medium;"><strong>Thu phí</strong></span></td>\r\n</tr>\r\n<tr>\r\n<td><span style="font-family: arial,helvetica,sans-serif; font-size: medium;"><span style="color: black;"><span style="color: black;">10 bộ máy tính</span></span></span></td>\r\n<td><span style="font-size: medium; font-family: arial,helvetica,sans-serif; color: black;">50km</span></td>\r\n<td><span style="font-size: medium; font-family: arial,helvetica,sans-serif; color: black;">Theo thỏa thuận với khách hàng</span></td>\r\n<td><span style="font-size: medium; font-family: arial,helvetica,sans-serif; color: black;">Từ km thứ 51 thu phí 10.000/km, giao hàng tối đa 150 km</span></td>\r\n</tr>\r\n<tr>\r\n<td><span style="font-size: medium; font-family: arial,helvetica,sans-serif; color: black;">11-20 bộ máy tính</span></td>\r\n<td><span style="font-size: medium; font-family: arial,helvetica,sans-serif; color: black;">100km</span></td>\r\n<td><span style="font-size: medium; font-family: arial,helvetica,sans-serif; color: black;">Theo thỏa thuận với khách hàng</span></td>\r\n<td><span style="font-size: medium; font-family: arial,helvetica,sans-serif; color: black;">Từ km thứ 101 thu phí 10.000/km, giao hàng tối đa 300 km</span></td>\r\n</tr>\r\n<tr>\r\n<td><span style="font-family: arial,helvetica,sans-serif; font-size: medium;"><span style="color: black;">21-30 bộ</span> máy tính</span></td>\r\n<td><span style="font-size: medium; font-family: arial,helvetica,sans-serif; color: black;">200km</span></td>\r\n<td><span style="font-size: medium; font-family: arial,helvetica,sans-serif; color: black;">Theo thỏa thuận với khách hàng</span></td>\r\n<td><span style="font-size: medium; font-family: arial,helvetica,sans-serif; color: black;">Từ km thứ 201 thu phí 10.000/km, giao hàng tối đa 300 km</span></td>\r\n</tr>\r\n<tr>\r\n<td><span style="font-size: medium; font-family: arial,helvetica,sans-serif; color: black;"><span style="color: black;">31 bộ trở lên</span></span></td>\r\n<td><span style="font-size: medium; font-family: arial,helvetica,sans-serif; color: black;">300km</span></td>\r\n<td><span style="font-size: medium; font-family: arial,helvetica,sans-serif; color: black;">Theo thỏa thuận với khách hàng</span></td>\r\n<td><span style="font-family: arial,helvetica,sans-serif; font-size: medium;"> </span></td>\r\n</tr>\r\n</tbody>\r\n</table><p>\r\n<br></p><p><span style="font-family: arial,helvetica,sans-serif; font-size: medium;"> <strong><span style="color: #222222;" lang="VI">3/</span></strong><strong><span style="color: #222222;"> Vận chuyển tính phí theo thỏa thuận:</span></strong></span></p><p>\r\n<br></p><p><span style="font-family: arial,helvetica,sans-serif; font-size: medium;"><strong><span style="color: black;">- </span></strong><span style="color: #222222;">Áp\r\n dụng với khách hàng không thuộc các trường hợp như mục 1.và 2. Nếu có \r\nnhu cầu mua hàng (mà khách ở xa, tỉnh lẻ) thì công ty sẽ chuyển hàng cho\r\n khách hàng theo hai hình thức ôtô, đường bưu điện .... và khách hàng sẽ\r\n tự chịu cước phí (cước phí theo thảo thuận). Có 2 cách thanh toán cước \r\nphí:</span></span></p><p>\r\n<br></p><p style="background: white none repeat scroll 0% 0%;"><span style="font-family: arial,helvetica,sans-serif; font-size: medium;"><strong><span style="color: #222222;">Cách 1: </span></strong><span style="color: #222222;">Khách\r\n hàng có thể chuyển trực tiếp tiền phí vận chuyển luôn cùng với tiền \r\nhàng vào tài khoản của công ty (Khách hàng ghi rõ chú thích phí vận \r\nchuyển khi chuyển tiền).</span></span></p><p>\r\n<br></p><p style="background: white none repeat scroll 0% 0%;"><span style="font-family: arial,helvetica,sans-serif; font-size: medium;"><a href="https://www.hanoicomputer.vn/download_media.php?id=48469" target="_blank"><span style="color: blue;">Tải về danh sách tài khoản ngân hàng dùng cho thanh toán của </span><span style="color: black;">HANOICOMPUTER</span></a></span></p><p>\r\n<br></p><p style="background: white none repeat scroll 0% 0%;"><span style="font-family: arial,helvetica,sans-serif; font-size: medium;"><strong><span style="text-decoration: underline;"><span style="color: #222222;">Cách 2</span></span></strong><strong><span style="color: #222222;">: </span></strong><span style="color: #222222;">Khách hàng tự thanh toán tiền cước phí trực tiếp cho ôtô khi nhận hàng.</span></span></p><p>\r\n<br></p><p style="background: white none repeat scroll 0% 0%;"><span style="font-family: arial,helvetica,sans-serif; font-size: medium;"><strong><span style="color: black;">- </span></strong><span style="color: #222222;">Sau khi kiểm tra có tiền của khách hàng trong tài khoản, chúng tôi sẽ gọi điện tới khách hàng để xác nhận v</span><span style="color: #222222;" lang="VI">à gửi hàng cho khách hàng.</span></span></p><p>\r\n<br></p><p style="text-align: justify; background: white none repeat scroll 0% 0%;"><span style="font-family: arial,helvetica,sans-serif; font-size: medium;"><strong><span lang="VI">4</span></strong><strong>. TRÁCH NHIỆM VỚI HÀNG HÓA VẬN CHUYỂN<span style="color: #666666;">.</span></strong></span></p><p>\r\n<br></p><p style="text-align: justify; background: white none repeat scroll 0% 0%;"><span style="font-family: arial,helvetica,sans-serif; font-size: medium;">-\r\n Công ty sẽ chịu trách nhiệm với hàng hóa và các rủi ro như mất mát hoặc\r\n hư hại của hàng hóa trong suốt quá trình vận chuyển hàng từ kho hàng \r\nchúng tôi đến nơi quý khách chỉ định.</span></p><p>\r\n<br></p><p style="text-align: justify; background: white none repeat scroll 0% 0%;"><span style="font-family: arial,helvetica,sans-serif; font-size: medium;">- Quý khách có trách nhiệm kiểm tra hàng hóa khi nhận hàng. Khi phát hiện hàng hóa bị hư hại, trầy xước, bể vỡ, m<span lang="VI">ó</span>p\r\n méo hoặc sai hàng hóa thì ký xác nhận tình trạng hàng hóa với Nhân viên\r\n giao nhận và thông báo ngay cho Bộ phận chăm sóc khách hàng theo số \r\nhotline của Công ty<span lang="VI"> (Hotline 097.858.0088)</span></span></p><p>\r\n<br></p><p style="text-align: justify; background: white none repeat scroll 0% 0%;"><span style="font-family: arial,helvetica,sans-serif; font-size: medium;">-\r\n Sau khi qúy khách đã ký nhận hàng mà không ghi chú hoặc có ý kiến về \r\nhàng hóa. Công ty không có trách nhiệm với những yêu cầu đổi trả vì hư \r\nhỏng, trầy xước, bể vỡ, m<span lang="VI">ó</span>p méo, sai hàng hóa,… từ quý khách sau này.</span></p><p>\r\n<br></p><p style="text-align: justify; background: white none repeat scroll 0% 0%;"><span style="font-family: arial,helvetica,sans-serif; font-size: medium;"><strong>5. CÁC ĐIỀU KIỆN KHÁC</strong></span></p><p>\r\n<br></p><p style="text-align: justify; background: white none repeat scroll 0% 0%;"><span style="font-family: arial,helvetica,sans-serif; background: white none repeat scroll 0% 0%; font-size: medium;">-\r\n Nếu địa chỉ giao hàng không rõ ràng, nằm trong ngõ ngách, hoặc ở những \r\nnơi nguy hiểm, những vùng đồi núi hiểm trở, phương tiện giao thông đi \r\nlại khó khăn, chúng tôi có quyền từ chối vận chuyển, giao nhận hàng trực\r\n tiếp.</span></p><p><br></p>', 'Công văn chấp thuận thoái vốn tại NHTMCP Đông Nam Á', '2017-01-05', '2017-04-08'),
(2, 'Hướng dẫn mua online', 'huong-dan-mua-online', 'news2.jpg', '<p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;">Quý khách có 3 cách để mua hàng trực tuyến tại http://hanoicomputer.vn</span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;">&nbsp;<strong><span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;">Cách 1: Đặt hàng qua điện thoại</span></strong></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;">&nbsp;<span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;">- Quý khách gọi đến số<span style="color: rgb(255, 0, 0);">&nbsp;<strong>1900.1903 bấm phím 1</strong></span></span><span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;">để gặp nhân viên bán hàng Online của chúng tôi. Nhân viên bán hàng Online sẽ tư vấn và tiếp nhận đơn hàng cho quý khách trong thời gian từ 8h-17h30 hàng ngày và tất cả các ngày trong tuần.</span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;">&nbsp;<span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;">- Với những đơn hàng ngoài giờ hành chính sẽ được xử lý vào hôm sau, hoặc quý khách có thể gọi vào số&nbsp;<span style="font-size: 13pt;"><span style="color: rgb(255, 0, 0);"><strong>1900.1903 bấm phím 0&nbsp;</strong></span></span><span style="color: black;">để được giải quyết</span></span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;">&nbsp;<strong><span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;">Cách 2: Đặt hàng qua phần mềm Chat trên website<br></span></strong></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;">&nbsp;<span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;">- Quý khách truy cập vào website:&nbsp;<strong>http://hanoicomputer.vn</strong>&nbsp;để chat và đặt hàng qua phần mềm chat hiển thị ngay góc bên phải chân trang web. Nhân viên Online sẽ tư vấn và xác nhận đơn hàng của quý khách.</span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><img src="https://www.hanoicomputer.vn/media/news/0310_hop-chat-subiz.jpg" alt="" style="border-width: initial; border-style: none; display: block; margin-left: auto; margin-right: auto;">&nbsp;</p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><strong><span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;">Cách 3: Đặt hàng qua hệ thống website của&nbsp;<span style="font-size: 13pt;"><span style="font-family: arial, helvetica, sans-serif; font-size: medium; color: rgb(0, 0, 0);">HANOICOMPUTER</span></span><br></span></strong></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;">&nbsp;<span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;">Có 2 cách để quý khách đặt hàng qua hệ thống website của&nbsp;<span style="font-size: 13pt;"><span style="font-size: 13pt;"><span style="font-family: arial, helvetica, sans-serif; font-size: medium; color: rgb(0, 0, 0);">HANOICOMPUTER</span></span></span><br></span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;">&nbsp;<strong><span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 1/ Đăng nhập tài khoản để mua hàng</span></strong></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;">&nbsp;<strong><span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2/ Mua hàng trực tiếp mà không cần đăng nhập</span></strong></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;">&nbsp;<strong><span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;">Quý khách Truy cập website http://hanoicomputer.vn và thực hiện các bước sau</span></strong></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;">&nbsp;<strong><span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;">Bước 1: Đăng nhập tài khoản ( Việc đăng nhập tài khoản giúp quý khách quản lý đơn hàng tốt hơn)</span></strong></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;">&nbsp;<span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;">- Muốn đặt hàng trực tuyến qua website quý vị cần phải đăng nhập tài khoản để thực hiện đặt hàng. Nếu chưa có tài khoản đăng nhập, Quý khách có thể Đăng Ký tài khoản&nbsp;<a href="https://www.hanoicomputer.vn/dang-ky" target="_blank" style="font-size: initial; cursor: pointer;"><span style="color: blue;">Tại đây</span></a>. Quý khách cần điền đầy đủ và chính xác các thông tin yêu cầu, các thông tin này là cơ sở để chúng tôi liên hệ với quý khách xác nhận đơn hàng. Sau khi hoàn tất việc đăng ký, hệ thống website sẽ gửi vào email mà khách hàng đăng ký 01 đường link kích hoạt tài khoản. Quý khách cần mở email ra để nhấn vào đường này để kích hoạt tài khoản. Sau khi kích hoạt, quý khách có thể sử dụng tài khoản này để đăng nhập và mua hàng.&nbsp;<strong><em>Lưu ý: khi quý khách chưa kích hoạt tài khoản thì chưa thể đăng nhập được tài khoản</em></strong></span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;">&nbsp;<img src="https://www.hanoicomputer.vn/media/news/3110_mua-hang-truc-tuyen2.jpg" alt="" style="border-width: initial; border-style: none; display: block; margin-left: auto; margin-right: auto;"></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><strong><span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;">&nbsp;</span></strong><span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;">- Quý khách đăng nhập tài khoản đã đăng ký, tìm kiếm và xem thông tin về sản phẩm muốn mua. Trên website có các công cụ tìm kiếm rất tiện lợi cho quý khách. Quý khách có thể tìm kiếm bằng từ khóa bằng cách gõ từ khóa cần tìm kiếm vào hộp tìm kiếm và gõ Enter. Hoặc vào menu danh mục sản phẩm nằm trên thanh menu của website lần lượt trỏ chuột vào các danh mục tên sản phẩm để lựa chọn sản phẩm phù hợp</span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;">&nbsp;<img src="https://www.hanoicomputer.vn/media/news/3110_mua-hang-truc-tuyen3.jpg" alt="" style="border-width: initial; border-style: none; display: block; margin-left: auto; margin-right: auto;"></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;">&nbsp;<span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;">&nbsp;- Nếu quý khách không muốn đăng nhập tài khoản thì có thể bỏ qua các bước đăng ký, đăng nhập tài khoản và chuyển qua bước chọn luôn sản phẩm cần mua rồi chuyển qua bước 2: Đặt hàng phía dưới</span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;">&nbsp;<strong><span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;">Bước 2: Đặt hàng</span></strong></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;">&nbsp;<span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;">- Khi đã chọn được sản phẩm ưng ý quý khách bấm vào sản phẩm đó để xem chi tiết thông tin sản phẩm, Tiếp theo quý khách sẽ có 2 lựa chọn là “&nbsp;<span style="color: red;">Mua Ngay”&nbsp;</span>hoặc “<span style="color: red;">Cho vào giỏ”</span>. Nếu tiếp tục mua hàng quý khách nhấn nút&nbsp;<span style="color: red;">“Cho vào giỏ”</span>. Sản phẩm của bạn sẽ được add vào giỏ hàng, quý khách tiếp tục xem và chọn lựa sản phẩm khác và&nbsp;<span style="color: red;">“Cho vào giỏ hàng”.</span>&nbsp;Quý khách có thể xem giỏ hàng của mình bằng cách nhấn nút&nbsp;<strong><span style="color: red;">Giỏ hàng&nbsp;</span></strong>ngay trên thanh toolbar góc phải phía trên cùng của website.&nbsp;</span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;">&nbsp;<img src="https://www.hanoicomputer.vn/media/news/3110_mua-hang-truc-tuyen4.jpg" alt="" style="border-width: initial; border-style: none; display: block; margin-left: auto; margin-right: auto;"></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;">&nbsp;- Trong giỏ hàng của quý khách có hiển thị thông tin về tên sản phẩm, đơn giá, số lượng cần mua, quý khách có thể thay đổi số lượng cần mua, xóa bỏ mặt hàng không muốn mua</span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;">&nbsp;<img src="https://www.hanoicomputer.vn/media/news/3110_mua-hang-truc-tuyen5.jpg" alt="" style="border-width: initial; border-style: none; display: block; margin-left: auto; margin-right: auto;"></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;">&nbsp;&nbsp;<span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;">Quý khách bấm<span style="color: red;">”Tiếp tục”</span>&nbsp;để thực hiện mua hàng trong giỏ</span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;">&nbsp;<img src="https://www.hanoicomputer.vn/media/news/3110_mua-hang-truc-tuyen6.jpg" alt="" style="border-width: initial; border-style: none; display: block; margin-left: auto; margin-right: auto;"></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;">&nbsp;<span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;">- Ở bước này nếu quý khách đã đăng nhập tài khoản thì các thông tin về khách hàng sẽ tự động nạp vào bảng thông tin khách hàng ở phía trên, nếu quý khách chưa đăng nhập tài khoản thì vui lòng điền đầy đủ thông tin.</span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;">&nbsp;<span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;">- Khi đã chốt được mặt hàng cần mua, quý khách nhấn nút “<strong><span style="color: red;">Đặt hàng”&nbsp;</span></strong><span style="color: rgb(0, 0, 0);"><span style="font-size: 13pt;">đơn hàng của quý khách sẽ được gửi đến hệ thống website của chúng tôi, hệ thống sẽ tự động gửi lại cho quý khách thông tin đơn hàng vừa đặt của quý khách vào địa chỉ email mà quý khách đã đăng ký</span></span></span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;">&nbsp;<span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;">- Nhân viên kinh doanh Online của chúng tôi sẽ liên hệ với khách hàng trong thời gian sớm nhất để xác nhận đơn hàng và xử lý đơn hàng theo yêu cầu của quý khách</span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;">&nbsp;<strong><span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;">Bước 3: Nhận hàng và thanh toán</span></strong></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;">&nbsp;<span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;">- Quý khách có thể tham khảo chính sách vận chuyển hàng&nbsp;<a href="https://www.hanoicomputer.vn/huong-dan-khach-hang/chinh-sach-van-chuyen-hang/a7.html" target="_blank" style="font-size: initial; cursor: pointer;"><span style="color: blue;">Tại Đây</span></a>&nbsp;và xem các hình thức thanh toán&nbsp;<a href="https://www.hanoicomputer.vn/huong-dan-khach-hang/huong-dan-thanh-toan/a650.html" target="_blank" style="font-size: initial; cursor: pointer;"><span style="color: blue;">Tại Đây</span></a></span></p>', 'Kế hoạch sản xuất kinh doanh và đầu tư phát triển 05 năm', '2017-01-05', '2017-04-15'),
(3, 'Hướng dẫn mua trả góp', 'huong-dan-mua-tra-gop', 'news3.jpg', '<p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px; margin-bottom: 0.0001pt;"><span style="font-size: 10pt;">Khách hàng là công dân Việt Nam tuổi từ&nbsp;21 đến 60 tuổi (riêng đối tượng Sinh Viên từ 18 trở lên) đều có thể tham gia mua hàng trả góp tại&nbsp;<span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;"><span style="font-family: arial, helvetica, sans-serif; font-size: medium; color: rgb(0, 0, 0);">HANOICOMPUTER</span></span>. Các ưu điểm khi mua hàng trả góp tại&nbsp;<span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;"><span style="font-family: arial, helvetica, sans-serif; font-size: medium; color: rgb(0, 0, 0);">HANOICOMPUTER</span></span>:</span></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">- Lãi suất 2,2% (tính theo dư nợ ban đầu).</span></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">- Kỳ hạn vay rộng (06, 09, 12, 18 ,24 tháng).</span></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">- Hỗ trợ đối tượng khách hàng có hộ khẩu ngoại tỉnh, Hà Nội đang sinh sống, học tập và làm việc tại Hà Nội.</span></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">- Đặc biệt hỗ trợ đối tượng đã nghỉ hưu, quân nhân và công an.</span></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><strong><span style="font-size: 10pt;">ĐỐI VỚI KHÁCH HÀNG LÀ SINH VIÊN:</span></strong></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">- Là công dân Việt Nam đang học tập tại Hà Nội.</span></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">- Từ 18 tuổi trở lên.</span></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">- Có người đứng ra bảo lãnh (người bảo lãnh là bố, mẹ, anh, chị, em ruột).</span></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">- Mức thu nhập của người đứng ra bảo lãnh từ 1,500,000 vnd trở lên.</span></p><ul type="disc" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><li class="MsoNormal"><span style="font-size: 10pt;">Nếu là bố, mẹ bảo lãnh: hộ khẩu của bố mẹ ở các tỉnh hoặc ở Hà Nội đều được.</span></li><li class="MsoNormal"><span style="font-size: 10pt;">Nếu là anh, chị, em ruột: phải đang sinh sống, làm việc tại Hà Nội.</span></li></ul><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><strong><span style="font-size: 10pt;">Những giấy tờ cần chuẩn bị:</span></strong></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">- CMND photo.</span></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">- Sổ hộ khẩu &nbsp;photo.</span></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">- Hóa đơn xác nhận địa chỉ nơi ở của người mua hàng.</span></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">- Thẻ sinh viên photo.</span></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">- CMND của người đứng ra bảo lãnh.</span></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><strong><span style="text-decoration-line: underline;"><span style="font-size: 10pt;">Chú ý:</span></span></strong></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">- Sinh viên ngoại tỉnh đang học tập tại Hà Nội được ưu tiên hỗ trợ mua trả góp.</span></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">- Người bảo lãnh không cần đến ký nhận giấy tờ (thuận tiện cho đối tượng có người bảo lãnh đang cư trú ở các tỉnh xa Hà Nội).</span></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">- Nếu vay trên 10 triệu,&nbsp; người đứng ra bảo lãnh phải cung cấp giấy tờ chứng minh thu nhập (giấy tờ rất đơn giản).</span></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">- Tất cả giấy tờ là bản photo không cần công chứng.</span></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">- Hóa đơn xác nhận địa chỉ nơi ở của người mua hàng: hóa đơn tiền điện, hóa đơn tiền nước, hóa đơn tiền inteet, hóa đơn truyền hình cáp, hóa đơn điện thoại di động trả sau, giấy tạm trú …</span></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><strong><span style="font-size: 10pt;">ĐỐI VỚI KHÁCH HÀNG LÀ NGƯỜI ĐÃ ĐI LÀM:</span></strong></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">- Là công dân Việt Nam đang làm việc tại Hà Nội.</span></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">- Từ 21 – 60 tuổi.</span></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">- Thu nhập từ 1,500,000 vnd trở lên.</span></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><strong><span style="font-size: 10pt;">Những giấy tờ cần chuẩn bị:</span></strong></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">- CMND photo.</span></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">- Sổ hộ khẩu &nbsp;photo.</span></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">- Hóa đơn xác nhận địa chỉ nơi ở của người mua hàng.</span></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><strong><span style="text-decoration-line: underline;"><span style="font-size: 10pt;">Chú ý:</span></span></strong></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">- Người đi làm có hộ khẩu ở Hà Nội hoặc ngoại tỉnh và đang làm việc tại Hà Nội cũng được hỗ trợ. Đặc biệt hỗ trợ đối tượng Công An, Bộ Đội, Giáo Viên …</span></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">- Nếu số tiền vay trên 10 triệu, người mua phải chứng minh thu nhập (giấy tờ rất đơn giản)</span></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">- Tất cả giấy tờ là bản photo không cần công chứng.</span></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">- Hóa đơn xác nhận địa chỉ nơi ở của người mua hàng: hóa đơn tiền điện, hóa đơn tiền nước, hóa đơn tiền inteet, hóa đơn truyền hình cáp, hóa đơn điện thoại di động trả sau, giấy tạm trú …</span></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><strong><span style="font-size: 10pt;">ĐỐI VỚI KHÁCH HÀNG LÀ CHỦ HỘ KINH DOANH:</span></strong></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">- Là công dân Việt Nam đang kinh doanh tại Hà Nội dưới hình thức cửa hàng hoặc công ty.</span></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">- Từ 21 – 60 tuổi.</span></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">- Thu nhập từ 1,500,000 vnd trở lên.</span></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><strong><span style="font-size: 10pt;">Những giấy tờ cần chuẩn bị:</span></strong></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">- CMND photo.</span></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">- Sổ hộ khẩu photo.</span></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">- Hóa đơn xác nhận địa chỉ nơi ở của người mua hàng.</span></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">- Giấy phép đăng ký kinh doanh photo.</span></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><strong><span style="text-decoration-line: underline;"><span style="font-size: 10pt;">Chú ý:</span></span></strong></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">- Chủ hộ kinh doanh có hộ khẩu ở Hà Nội hoặc Ngoại tỉnh và đang kinh doanh tại Hà Nội cũng được hỗ trợ.</span></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">- Nếu số tiền vay trên 10 triệu, người mua phải chứng minh thu nhập (giấy tờ rất đơn giản).</span></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">- Tất cả giấy tờ là bản photo không cần công chứng.</span></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">- Hóa đơn xác nhận địa chỉ nơi ở của người mua hàng: hóa đơn tiền điện, hóa đơn tiền nước, hóa đơn tiền inteet, hóa đơn truyền hình cáp, hóa đơn điện thoại di động trả sau, giấy tạm trú …</span></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><strong><span style="font-size: 10pt;">ĐĂNG KÝ ONLINE.</span></strong></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">- Khách hàng có thể tải Form đăng ký trên website của&nbsp;<span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;"><span style="font-family: arial, helvetica, sans-serif; font-size: medium; color: rgb(0, 0, 0);">HANOICOMPUTER</span></span><br></span></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px; text-align: center;"><a href="http://hanoicomputer.vn/media/lib/formdangky.doc" target="_blank" style="font-size: initial; cursor: pointer;"><strong><span style="color: red;"><span style="font-size: 14pt;"><span style="font-size: 36pt;"><span style="font-size: 36pt;"><span style="font-size: 10pt;">Click vào đây để &nbsp;</span><span style="font-size: 10pt; color: blue;">Download</span></span></span></span></span></strong></a></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">- Điền đủ thông tin vào Form và gửi vào hòm thư:&nbsp;<strong>tragop@hanoicomputer.com</strong></span></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">- Kết quả sẽ được trả lời từ 30 phút đến 02 ngày (trừ thứ 7 và chủ nhật).</span></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">- Nếu không nhận được kết quả có thể gọi số sau để hỏi về tình trạng hồ sơ:</span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><strong>Trả góp tại 43 Thái Hà: 043.538.0088 (Máy lẻ: 21)&nbsp;<br></strong></p><strong style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;">Trả góp tại 131 Lê Thanh Nghị: 043.628.0886 máy lẻ 48</strong><br style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">- Quý khách có thể tham khảo mức tiền đóng hàng tháng cho kỳ hạn mà mình đăng ký mua trả góp trên website như sau:</span></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><strong><span style="font-size: 10pt;">Bước 1: Lựa chọn sản phẩm mà khách hàng muốn mua.</span></strong></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">Khi muốn mua trả góp một sản phẩm nào đó, quý khách bấm chọn vào xem chi tiết sản phẩm,&nbsp; sẽ thấy nút trả góp ngay bên cạnh hình ảnh của sản phẩm ( Xem hình). Bấm vào nút trả góp sẽ chuyển sang trang trả góp<br></span></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;"><img src="https://www.hanoicomputer.vn/media/news/1511_tragop-huongdan.jpg" alt="" width="700" style="border-width: initial; border-style: none;"><br></span></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><strong><span style="font-size: 10pt;">Bước 2: Chọn mức tiền trả trước và kỳ hạn trả góp</span></strong></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><strong><span style="font-size: 10pt;"><img src="https://www.hanoicomputer.vn/media/news/1511_tragop-huongdan1.jpg" alt="" width="700" style="border-width: initial; border-style: none;"></span></strong></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">- Khách hàng điền thông tin mức tiền trả trước, kỳ hạn trả góp. Hệ thống sẽ hiển thị số tiền bạn cần thanh toán hàng tháng, Tiếp theo chọn nhà cung cấp dịch vụ trả góp, và hoàn thành các thông tin trả góp sau đó bấm "Đăng Ký"<br></span></p><strong style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">Chuẩn bị hồ sơ.</span></strong><span style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"></span><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">- Ngay khi&nbsp;<span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;"><span style="font-family: arial, helvetica, sans-serif; font-size: medium; color: rgb(0, 0, 0);">HANOICOMPUTER</span></span>&nbsp;nhận được Form đăng ký, hồ sơ sẽ được thẩm định.</span></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">- Kết quả của hồ sơ sẽ được thông báo trực tiếp cho khách hàng bằng điện thoại.</span></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">- Khách hàng nên ghi lại những giấy tờ&nbsp;<span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;"><span style="font-family: arial, helvetica, sans-serif; font-size: medium; color: rgb(0, 0, 0);">HANOICOMPUTER</span></span>&nbsp;thông báo và mang đủ đến cho tư vấn viên của&nbsp;<span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;"><span style="font-family: arial, helvetica, sans-serif; font-size: medium; color: rgb(0, 0, 0);">HANOICOMPUTER</span></span><br></span></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">- Khách hàng nên liên lạc với tư vấn viên tại cửa hàng trước khi đến nhận bàn giao sản phẩm:</span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><strong>Trả góp tại 43 Thái Hà: 043.538.0088 (Máy lẻ: 21)&nbsp;<br></strong></p><strong style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;">Trả góp tại 131 Lê Thanh Nghị: 043.628.0886 máy lẻ 48</strong><br style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">&nbsp;- Trong vòng 30 – 45 phút, nhân viên tư vấn sẽ giúp khách hàng hoàn thành thủ tục mua hàng trả góp.</span></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><strong><span style="font-size: 10pt;">ĐĂNG KÝ TẠI CỬA HÀNG.</span></strong></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">Khách hàng có thể đến các showroom của&nbsp;<span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;"><span style="font-family: arial, helvetica, sans-serif; font-size: medium; color: rgb(0, 0, 0);">HANOICOMPUTER</span></span>&nbsp;để đăng ký mua hàng trả góp tại các địa chỉ sau:</span></p><ul type="disc" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><li class="MsoNormal"><span style="font-size: 10pt;"><strong><span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;"><span style="font-family: arial, helvetica, sans-serif; font-size: medium; color: rgb(0, 0, 0);">HANOICOMPUTER</span></span>&nbsp;– 129 + 131 Lê Thanh Nghị - Hai Bà Trưng - Hà Nội</strong></span></li><li class="MsoNormal"><span style="font-size: 10pt;"><strong><span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;"><span style="font-family: arial, helvetica, sans-serif; font-size: medium; color: rgb(0, 0, 0);">HANOICOMPUTER</span></span>&nbsp;– 43 Thái Hà - Đống Đa - Hà Nội</strong></span></li></ul><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">Khách hàng có thể liên hệ với bộ phận tiếp nhận trả góp để được giải đáp</span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><strong>Trả góp tại 43 Thái Hà: 043.538.0088 (Máy lẻ: 21)&nbsp;<br></strong></p><strong style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;">Trả góp tại 131 Lê Thanh Nghị: 043.628.0886 máy lẻ 48</strong><br style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">Email:</span><strong><span style="font-size: 10pt;">&nbsp;tragop@hanoicomputer.com&nbsp;</span></strong></p><p class="MsoNormal" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 10pt;">Yahoo chat:</span><strong><span style="font-size: 10pt;">&nbsp;tragop1_hnc&nbsp;</span></strong><span style="font-size: 10pt;">hoặc</span><strong><span style="font-size: 10pt;">&nbsp;tragop2_hnc</span></strong></p>', 'Báo cáo quyết toán 6 tháng đầu năm 2016', '2017-01-05', '2017-04-15');
INSERT INTO `news` (`id`, `title`, `titlekd`, `img`, `content`, `sum`, `created_at`, `updated_at`) VALUES
(4, 'Quy định về đổi, trả lại hàng', 'quy-dinh-ve-doi-tra-lai-hang', 'news4.jpg', '<p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="color: rgb(0, 0, 0); font-family: arial, helvetica, sans-serif; font-size: medium;"><strong>1/ Định nghĩa nhập lại:</strong></span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="color: rgb(0, 0, 0); font-family: arial, helvetica, sans-serif; font-size: medium;">Nhập lại là việc Công ty Cổ Phần Máy Tính Hà Nội nhận lại hàng hóa đã bán cho khách hàng và thanh toán tiền hoặc đổi hàng hóa khác có giá trị tương đương hoặc có giá trị chênh lệch cho khách hàng đó.<br><strong>2. Điều kiện nhập lại hàng:</strong></span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="color: rgb(0, 0, 0); font-family: arial, helvetica, sans-serif; font-size: medium;"><strong><img src="https://www.hanoicomputer.vn/media/news/1304_Do-hoa-tra-hang-bao-hanh-1.jpg" alt="" width="900" style="border-width: initial; border-style: none; display: block; margin-left: auto; margin-right: auto;"></strong></span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="color: rgb(0, 0, 0); font-family: arial, helvetica, sans-serif; font-size: medium;"><strong>&nbsp;</strong></span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="color: rgb(0, 0, 0); font-family: arial, helvetica, sans-serif; font-size: medium;">- Hàng hóa đã mua trong vòng&nbsp;03 ngày&nbsp;tính đến thời điểm nhập lại của khách hàng (<strong>không áp dụng với Laptop)</strong>, khi nhập lại khách hàng được hoàn trả lại tiền.</span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="color: rgb(0, 0, 0); font-family: arial, helvetica, sans-serif; font-size: medium;">- Sau&nbsp;03 ngày, khách hàng chỉ được trả lại hàng hoặc đổi sang mặt hàng khác khi:</span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="color: rgb(0, 0, 0); font-family: arial, helvetica, sans-serif; font-size: medium;">+&nbsp;Đối với các sản phẩm đã mua trong vòng 15 ngày:&nbsp;Hàng bị lỗi không phải nguyên nhân từ phía khách hàng, đã được Công ty khắc phục nhưng không có kết quả hoặc được nhận bảo hành với thời gian quá với quy chế được Trung tâm bảo hành của Công ty xác nhận.</span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="color: rgb(0, 0, 0); font-family: arial, helvetica, sans-serif; font-size: medium;">+ Đối với các sản phẩm đã mua ngoài thời hạn 15 ngày việc nhập lại hoặc đổi sang mặt hàng khác với chi phí và cách thức được thỏa thuận với khách hàng sẽ do Giám đốc Trung tâm bảo hành quyết định.</span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="color: rgb(0, 0, 0); font-family: arial, helvetica, sans-serif; font-size: medium;">&nbsp;- Hàng nhập lại phải đảm bảo các yếu tố sau:</span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="color: rgb(0, 0, 0); font-family: arial, helvetica, sans-serif; font-size: medium;">+ Hàng nhập lại phải sạch sẽ, còn nguyên tem, Serial, phiếu bảo hành, nguyên hình dạng, không xước, biến dạng, cháy nổ…</span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="color: rgb(0, 0, 0); font-family: arial, helvetica, sans-serif; font-size: medium;">+ Hàng phải còn đầy đủ vỏ hộp, sách hướng dẫn, đĩa cài đặt (nếu có) và các phụ kiện đi kèm.</span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="color: rgb(0, 0, 0); font-family: arial, helvetica, sans-serif; font-size: medium;"><span style="color: rgb(255, 0, 0);"><strong>Ghi chú:</strong></span><strong><em>&nbsp;Khách hàng vui lòng mang sản phẩm cần nhập lại qua nơi mua để được giải quyết theo quy định.</em></strong></span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;">&nbsp;</p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="color: rgb(0, 0, 0); font-family: arial, helvetica, sans-serif; font-size: medium;"><strong><img src="https://www.hanoicomputer.vn/media/news/1304_Do-hoa-tra-hang-bao-hanh2.jpg" alt="" width="900" style="border-width: initial; border-style: none; display: block; margin-left: auto; margin-right: auto;"></strong></span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;">&nbsp;</p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="color: rgb(0, 0, 0); font-family: arial, helvetica, sans-serif; font-size: medium;"><strong>3. Giá nhập lại hoặc đổi lại:</strong></span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;">&nbsp;</p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="color: rgb(0, 0, 0); font-family: arial, helvetica, sans-serif; font-size: medium;">- Hàng mua trong vòng&nbsp;03 ngày&nbsp;kể từ ngày mua hàng sẽ được nhập lại theo giá tại thời điểm trả lại hàng (tính theo báo giá linh kiện của Công ty). Trường hợp giá tại thời điểm nhập lại cao hơn giá thời điểm khách mua hàng thì giá nhập lại được căn cứ vào giá tại thời điểm mua hàng.</span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="color: rgb(0, 0, 0); font-family: arial, helvetica, sans-serif; font-size: medium;">- Giá hàng hóa nhập đổi cũng được tính theo nguyên tắc nêu trên.</span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="color: rgb(0, 0, 0); font-family: arial, helvetica, sans-serif; font-size: medium;">- Trường hợp tại thời điểm nhập lại hàng mà loại hàng đó không có trên thị trường hoặc không định được giá của mặt hàng đó thì sẽ cùng thỏa thuận một mức giá hợp lý, cùng căn cứ mức giá này để tính toán.</span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="color: rgb(0, 0, 0); font-family: arial, helvetica, sans-serif; font-size: medium;">- Những trường hợp đặc biệt về nhập lại hàng hóa&nbsp;mà Giám đốc Trung tâm bảo hành không quyết định được&nbsp;sẽ do Tổng Giám đốc quyết định.</span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="color: rgb(0, 0, 0); font-family: arial, helvetica, sans-serif; font-size: medium;"><strong>4. Thủ tục nhập lại</strong></span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="color: rgb(0, 0, 0); font-family: arial, helvetica, sans-serif; font-size: medium;">- Bắt buộc khách hàng phải xuất trình phiếu xuất kho tại Kho và hóa đơn tài chính (nếu có) của mặt hàng cần nhập lại.</span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="color: rgb(0, 0, 0); font-family: arial, helvetica, sans-serif; font-size: medium;">- Trường hợp sản phẩm đã được Công ty Cổ phần Máy Tính Hà Nội viết hóa đơn tài chính, khách hàng muốn trả lại hàng phải viết trả lại hoá đơn tài chính hoặc làm thủ tục hủy hóa đơn này theo quy định của pháp luật.</span></p>', 'Báo cáo chế độ tiền lương, tiền thưởng năm 2015', '2017-01-05', '2017-04-15'),
(5, 'Chính sách bảo hành', 'chinh-sach-bao-hanh', 'news6.jpg', '<p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: medium;"><strong><span style="font-family: arial, helvetica, sans-serif;">I. Chính sách bảo hành (Áp dụng cho khách hàng sử dụng cuối):</span></strong></span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: medium; font-family: arial, helvetica, sans-serif;"> <strong>1. Đối với laptop, màn hình và linh kiện:</strong></span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: medium; font-family: arial, helvetica, sans-serif;">-  Được đổi ngay sản phẩm mới 100% trong vòng 15 ngày đầu sau khi mua hàng (sản phẩm phải có đầy đủ vỏ hộp, phụ kiện kèm theo và không bị trầy xước và không phải là sản phẩm tiêu hao).</span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: medium; font-family: arial, helvetica, sans-serif;">- Cam kết khắc phục sự cố và trả hàng bảo hành trong thời gian 7 ngày (không tính thứ 7, chủ nhật, các ngày lễ, tết  và các trường hợp khác được thỏa thuận trước) cho các sản phẩm đã mua từ ngày thứ 16 cho đến khi hết thời hạn bảo hành.</span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: medium; font-family: arial, helvetica, sans-serif;"><br></span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: medium;"><strong><span style="font-family: arial, helvetica, sans-serif;">2. Những trường hợp bảo hành muộn hơn so với quy định:</span></strong></span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: medium; font-family: arial, helvetica, sans-serif;">- Trong trường hợp thời gian trả bảo hành muộn hơn thời gian quy định thì chúng tôi sẽ cộng thêm vào thời hạn bảo hành sản phẩm của Quý khách thời gian tương ứng với thời gian chậm trả cho khách hàng.</span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: medium; font-family: arial, helvetica, sans-serif;">- Các sản phẩm được bảo hành theo quy định của hãng, của nhà cung cấp mà bảo hành lâu thì Trung tâm Bảo hành có trách nhiệm đảm bảo luôn đôn đốc hãng trả bảo hành trong thời gian sớm nhất cho Quý khách hàng và liên lạc với khách hàng ngay khi hãng trả bảo hành. Quý khách có thể được mượn một thiết bị tương đương trong thời gian chờ bảo hành (nếu có sẵn hàng cho mượn).</span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;">Trường hợp không bảo hành được hoặc thời gian bảo hành quá lâu, Quý khách có thể chọn lựa một trong các phương án sau:</span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: medium; font-family: arial, helvetica, sans-serif;">              + Đổi sang thiết bị khác tương đương hoặc có cấu hình cao hơn với chi phí thỏa thuận.</span><br><span style="font-size: medium; font-family: arial, helvetica, sans-serif;">              + Nhập lại sản phẩm theo giá thỏa thuận.</span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;"> <em>Lưu ý:</em></span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: medium; font-family: arial, helvetica, sans-serif;"><em>           - </em>Chính sách <em>trên chỉ áp dụng với sản phẩm bị lỗi do hãng sản xuất và đủ điều kiện</em> bảo hành<em>.</em></span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;"><em>         -  Công ty Cổ Phần Máy tính Hà Nội không</em> bảo hành <em>và không chịu trách nhiệm về dữ liệu và tính hợp pháp của các phần mềm, dữ liệu hiện có trong sản phẩm của khách hàng. Quý khách hàng vui lòng lưu lại dữ liệu của mình trước khi gửi</em> bảo hành<em>.</em></span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;"><em><br></em></span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;"><em> </em><strong>II. Điều kiện bảo hành:</strong></span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: medium; font-family: arial, helvetica, sans-serif;"> 1. Những trường hợp sau đây sẽ không được bảo hành:</span><br><span style="font-size: medium; font-family: arial, helvetica, sans-serif;">            + Thiết bị không có phiếu bảo hành, phiếu bảo hành  đã bị sửa đổi thông tin, bị chắp nối, rách nát.</span><br><span style="font-size: medium; font-family: arial, helvetica, sans-serif;">            + Các thiết bị không còn nguyên tem bảo hành và mã vạch của hãng sản xuất, nhà phân phối và của Công ty Cổ Phần Máy Tính Hà Nội.</span><br><span style="font-size: medium; font-family: arial, helvetica, sans-serif;">            + Sử dụng điện áp sai quy định, quá công suất của thiết bị hoặc bị cháy nổ, rơi vỡ, móp, nứt, thủng, trầy xước…</span><br><span style="font-size: medium; font-family: arial, helvetica, sans-serif;">            + Hư hỏng do thiên tai, động vật, côn trùng và con người gây ra.</span><br><span style="font-size: medium; font-family: arial, helvetica, sans-serif;">            + Các phụ kiện tiêu hao.</span><br><span style="font-size: medium; font-family: arial, helvetica, sans-serif;">            + Tất cả các lỗi về phần mềm.</span><br><span style="font-size: medium; font-family: arial, helvetica, sans-serif;">            + Hết thời hạn bảo hành ghi trên phiếu.</span><br><span style="font-size: medium; font-family: arial, helvetica, sans-serif;">            + Khách hàng tự ý can thiệp vào BIOS.</span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: medium; font-family: arial, helvetica, sans-serif;"><br></span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: medium; font-family: arial, helvetica, sans-serif;"> 2. Các trường hợp khác sẽ được bảo hành theo tiêu chuẩn của hãng sản xuất.</span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;"><strong>III. Chính sách ưu đãi:</strong></span><br><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;">- Những linh kiện hết thời gian bảo hành hoặc không đủ điều kiện bảo hành, chúng tôi sẽ tạo điều kiện hỗ trợ tới mức tối đa.</span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;">- Khách hàng lẻ khi đến gửi hàng bảo hành tại trung tâm bảo hành được lựa chọn 1 trong 2 hình thức nhận hàng:</span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;">+ Tại nơi sử dụng hoặc tại nơi gửi hàng:Trung tâm Bảo hành sẽ có trách nhiệm đem trả hàng bảo hành (sau khi đã xử lý xong) tại nơi khách hàng sử dụng hoặc nơi khách hàng đăng ký (trong vòng bán kính 20 km tính từ trung tâm bảo hành).</span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;">+ Tại Trung tâm Bảo hành của Công ty: Quy trình nhận, trả hàng vẫn theo quy định hiện hành.</span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;"><br></span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;"><strong>IV. Địa điểm bảo hành:</strong></span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;"><strong>Trung Tâm Bảo Hành Công ty Cổ Phần Máy Tính Hà Nội – Tầng 2, Số 45 Thái Hà, Trung Liệt, Đống Đa, Hà Nội</strong></span><br><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;"><span data-mce-mark="1"><strong>Điện thoại:  <strong>043.224.7486 / 043.628.4883</strong></strong></span></span><br><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;">Email: <a href="mailto:baohanhhnc@hanoicomputer.com" target="_blank" style="font-size: initial; cursor: pointer;">baohanhhnc@hanoicomputer.com</a>     YM: baohanh_hnc</span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;">* <span style="color: rgb(255, 0, 0);">Tra cứu thông tin bảo hành</span>:<strong> 0961.660.638</strong></span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;"><strong>* Khiếu nại về bảo hành: <strong>0979.851.981</strong></strong></span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;"><span data-mce-mark="1"><strong>Trung Tâm Bảo Hành</strong></span> làm việc liên tục từ 8h đến 19h tất cả các ngày trong tuần ( Ngày <span style="color: rgb(255, 0, 0);">Chủ Nhật <span data-mce-mark="1">từ 8h đến 17h30</span></span>), mọi đóng góp ý kiến về chất lượng sản phẩm hoặc dịch vụ bảo hành của Công ty, Quý khách vui lòng liên hệ số điện thoại chăm sóc khách hàng: <a href="tel:04.36280886" target="_blank" style="font-size: initial; cursor: pointer;">04.36280886</a>/ máy lẻ 4 / Hotline: 097.858.0088  hoặc Email: <a href="mailto:cskh@hanoicomputer.com" target="_blank" style="font-size: initial; cursor: pointer;">cskh@hanoicomputer.com</a></span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: medium; font-family: arial, helvetica, sans-serif;"><strong>Cảm ơn Quý khách đã sử dụng dịch vụ của chúng tô</strong></span></p>', 'Báo cáo tình hình sắp xếp, đổi mới doanh nghiệp năm 2015', '2017-01-05', '2017-04-15'),
(6, 'Tuyển dụng', 'tuyen-dung', 'NB67_baner.png', '<p align="center" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px; text-align: center;"><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;"><strong>THÔNG TIN TUYỂN DỤNG THÁNG 4/2017</strong></span></p><p align="right" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px; text-align: center;"><span data-mce-mark="1" style="color: rgb(255, 51, 51); font-size: medium; font-family: arial, helvetica, sans-serif;"><strong><span data-mce-mark="1" style="color: blue;"> </span></strong>Hạn nộp Hồ sơ: 30/04/2017 – ƯU TIÊN ỨNG VIÊN NỘP HỒ SƠ SỚM <br></span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span data-mce-mark="1" style="color: rgb(0, 0, 0); font-size: medium; font-family: arial, helvetica, sans-serif;">Công ty Cổ phần Máy tính Hà Nội là đơn vị hàng đầu trong lĩnh vực bán lẻ sản phẩm Máy tính, linh kiện điện tử. Đề mở rộng hoạt động sản xuất kinh doanh, hiện nay chúng tôi tuyển dụng các vị trí làm việc tại Hà Nội như sau:</span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;"><strong><span data-mce-mark="1" style="color: red;"> </span></strong></span></p><table border="1" cellspacing="0" cellpadding="0" align="center" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px; width: 768px; height: 1759px;"><tbody><tr style="height: 21.3pt;"><td valign="top" width="108" style="padding: 2.75pt 2.75pt 2.75pt 2.7pt; width: 81pt; height: 21.3pt;"><p class="TableContents" align="center" style="text-align: center;"><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;"><strong><span data-mce-mark="1" style="color: red;">Vị trí tuyển</span></strong></span></p></td><td valign="top" width="294" style="padding: 2.75pt 2.75pt 2.75pt 2.7pt; width: 220.45pt; height: 21.3pt;"><p class="TableContents" align="center" style="text-align: center;"><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;"><strong><span data-mce-mark="1" style="color: red;">Mô tả công việc</span></strong></span></p></td><td valign="top" width="264" style="padding: 2.75pt 2.75pt 2.75pt 2.7pt; width: 2.75in; height: 21.3pt; border: 1pt solid black;"><p class="TableContents" align="center" style="text-align: center;"><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;"><strong><span data-mce-mark="1" style="color: red;">Yêu cầu công việc</span></strong></span></p></td></tr><tr><td valign="top" width="108" style="padding: 2.75pt 2.75pt 2.75pt 2.7pt; width: 81pt;"><p class="TableContents" align="center" style="text-align: center;"><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;"><strong><strong>01 Trưởng Phòng Marketing</strong></strong></span></p><p class="TableContents" align="center" style="text-align: center;"><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;"> </span></p></td><td valign="top" width="294" style="padding: 2.75pt 2.75pt 2.75pt 2.7pt; width: 220.45pt;"><p><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;"> * Phát triển khách hàng mục tiêu.</span></p><p><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;">- Tổ chức hệ thống thu thập thông tin và lập hồ s<span lang="VI" data-mce-mark="1">ơ danh sách khách hàng mục tiêu.</span></span></p><p><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;">- Tổ chức quản lý và chăm sóc khách hàng.</span></p><p><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;">- Hỗ trợ và cung cấp thông tin khách hàng cho phòng kinh doanh. </span></p><p><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;">* Nghiên cứu thị tr<span lang="VI" data-mce-mark="1">ường, mở rộng thị trường, phát triển sản phẩm.</span></span></p><p><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;">- Lập kế hoạch nghiên cứu thị tr<span lang="VI" data-mce-mark="1">ường, mở rộng thị trường.</span></span></p><p><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;">- Xây dựng và thực hiện ch<span lang="VI" data-mce-mark="1">ương tr</span>ình marketing.</span></p><p><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;">- Đề xuất các sản phẩm phù hợp với nhu cầu của khách hàng và thị tr<span lang="VI" data-mce-mark="1">ường trong một khoảng thời gian nhất định.</span></span></p><p><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;">* Phát triển th<span lang="VI" data-mce-mark="1">ương hiệu, phát triển sản phẩm của công ty.</span></span></p><p><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;">- Đề xuất cho BGĐ về việc phát triển thương hiệu.</span></p><p><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;">- Hồ trợ cho các bộ phận khác thực hiện theo các ch<span lang="VI" data-mce-mark="1">ương tr</span>ình marketing.</span></p><p><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;">* Thực hiện các ch<span lang="VI" data-mce-mark="1">ương tr</span>ình marketing do ban giám đốc phê duyệt.</span></p><p><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;">- Chủ động xây dựng kế hoạch marketing ngắn hạn, trung hạn, dài hạn.</span></p><p><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;">- Tổ chức thực hiện ch<span lang="VI" data-mce-mark="1">ương tr</span>ình marketing.</span></p><p><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;">- Theo dõi, điều chỉnh, báo cáo tình hình thực hiện.</span></p><p><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;">* Kiểm soát công việc của nhân viên bộ phận mình, đề xuất khen thưởng, kỷ luật nhân viện thuộc bộ phận.</span></p><p><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;">- Giao việc cho nhân viên thuộc phạm vi quản lý</span></p><p><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;">- Kiểm tra giám sát nhân viên.</span></p><p><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;">- Đề xuất khen thưởng, kỷ luật, xét tăng lương nhân viên theo quy chế công ty.</span></p><p><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;">- Phân công, điều động nhân sự thuộc phạm vi bộ phận mình.</span></p><p><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;"> </span></p></td><td valign="top" width="264" style="padding: 2.75pt 2.75pt 2.75pt 2.7pt; width: 2.75in;"><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;"> </span><p align="center"><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif; color: black;">Nam/nữ tuổi từ 30 trở lên,</span></p><p align="center"><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif; color: black;">Tốt nghiệp CĐ trở lên chuyên nghành Marketing ,</span></p><p align="center"><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif; color: black;">Có kinh nghiệm 02 năm ở vị trí tương đương.</span></p><p align="center"><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif; color: black;">Năng lực / kỹ năng: Có khả năng quản lý, điều hành nhóm tốt</span></p><p align="center"><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif; color: black;">Các yêu cầu khác: Sáng tạo trong công việc, khả năng tự ứng xử giải quyết tốt công việc đột xuất</span></p><p align="center"><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif; color: black;">Ưu tiên: Ứng viên có kinh nghiệm và kiến thức chuyên môn trong vị trí công tác</span></p></td></tr><tr><td valign="top" width="108" style="padding: 2.75pt 2.75pt 2.75pt 2.7pt; width: 81pt;"><p class="TableContents" align="center" style="text-align: center;"> </p><p class="m_5756220858820078627gmail-TableContents" align="center" style="text-align: center; line-height: 13.8px;"><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;"><strong>Nhân viên Kỹ thuật</strong></span></p><p class="TableContents" align="center" style="text-align: center;"> </p><p class="m_5756220858820078627gmail-TableContents" align="center" style="text-align: center; line-height: 13.8px;"><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;"><strong> </strong></span></p><p class="TableContents" align="center" style="text-align: center;"><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;"><strong><span data-mce-mark="1" style="color: black;">Số lượng: 01</span></strong></span></p></td><td valign="top" width="294" style="padding: 2.75pt 2.75pt 2.75pt 2.7pt; width: 220.45pt;"><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;">-<span data-mce-mark="1" style="color: black;"> Tham gia vận chuyển hàng hóa, thực hiện giao, lắp, cài đặt thiết bị và các thủ tục giao nhận, thanh toán tại địa chỉ khách hàng.</span></span><p><span data-mce-mark="1" style="font-family: arial, helvetica, sans-serif; color: black; font-size: medium;">- Lắp ráp bộ máy, cài đặt, kiểm tra, khắc phục lỗi của thiết bị máy tính tại SR<br>- Tư vấn các giải pháp, hỗ trợ kỹ thuật cho khách hàng,<br>- Hướng dẫn khách hàng sử dụng sản phẩm.<br>- Thực hiện các công việc kỹ thuật khác.</span></p></td><td valign="top" width="264" style="padding: 2.75pt 2.75pt 2.75pt 2.7pt; width: 2.75in;"><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;"> <span data-mce-mark="1" style="color: black;">- Nam tuổi dưới 35 tuổi, Sức khỏe tốt.<br>- Tốt nghiệp từ trung cấp trở lên chuyên ngành kỹ thuật, CNTT.<br>- Hiểu biết tốt về các thiết bị IT, Điện tử, Các thiết bị tin học<br>- Chịu được áp lực công việc, trung thực, chịu khó<br>- Ưu tiên ứng viên có kinh nghiệm.</span></span></td></tr><tr><td valign="top" width="108" style="padding: 2.75pt 2.75pt 2.75pt 2.7pt; width: 81pt;"><p class="TableContents" align="center" style="text-align: center;"><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;"><strong><span data-mce-mark="1" style="color: black;">01 Nhân viên Kinh Doanh Camera</span></strong></span></p></td><td valign="top" width="294" style="padding: 2.75pt 2.75pt 2.75pt 2.7pt; width: 220.45pt;"><div><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;">- Tìm kiếm và khai thác khách hàng.</span></div><div><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;">- Đàm phán, tư vấn, thuyết phục khách hàng.</span></div><div><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;">- Duy trì, phát triển quan hệ với các khách hàng hiện tại, đảm bảo mối quan hệ hợp tác lâu dài với công ty</span></div><div><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;">- Phối hợp chặt chẽ với các phòng ban trong công ty.</span></div><div><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;">- Báo cáo, tham vấn cho Trưởng phòng kinh doanh các hoạt động của thị trường và đối thủ cạnh tranh.</span></div><div><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;">- Chi tiết khác trao đổi trong buổi phỏng vấn.</span></div></td><td valign="top" width="264" style="padding: 2.75pt 2.75pt 2.75pt 2.7pt; width: 2.75in;"><div><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;">- Nam/nữ tuổi từ 18-35, có phương tiện đi lại, có máy tính cá nhân.</span></div><div><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;">- Tốt nghiệp Trung Cấp trở lên, ưu tiên ứng viên tốt nghiệp các chuyên ngành kinh tế. Với những trường hợp đặc biệt có thể xét tốt nghiệp cấp 3 (trường hợp này sẽ được xét dựa trên năng lực cụ thể)</span></div><div><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;">- Ngoại hình khá, yêu thích công việc kinh doanh, nhanh nhẹn, năng động, sáng tạo. ĐẶC BIỆT: Chịu được áp lực công việc.</span></div><div><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;">- Có kỹ năng bán hàng, đàm phán, khả năng thuyết phục cao.</span></div><div><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;">- Ưu tiên các ứng viên đã có kinh nghiệm làm việc tại vị trí tương đương.</span></div></td></tr><tr><td valign="top" width="108" style="padding: 2.75pt 2.75pt 2.75pt 2.7pt; width: 81pt;"><p class="TableContents" align="center" style="text-align: center;"><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;"> </span></p><p align="center" style="text-align: center;"><span data-mce-mark="1" style="color: rgb(0, 0, 0); font-size: medium; font-family: arial, helvetica, sans-serif;"><strong>Nhân viên Kinh doanh Phân phối</strong></span></p><p class="TableContents" align="center" style="text-align: center;"><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;"> </span></p><p class="TableContents" align="center" style="text-align: center;"><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;"><strong>Số lượng: <strong>02 người</strong></strong></span></p></td><td valign="top" width="294" style="padding: 2.75pt 2.75pt 2.75pt 2.7pt; width: 220.45pt;"><span data-mce-mark="1" style="color: rgb(0, 0, 0); font-size: medium; font-family: arial, helvetica, sans-serif;"> </span><p><span data-mce-mark="1" style="color: rgb(0, 0, 0); font-size: medium; font-family: arial, helvetica, sans-serif;"><span data-mce-mark="1" style="background-image: none; background-position: 0% 0%; background-size: initial; background-repeat: repeat; background-attachment: scroll; background-origin: initial; background-clip: initial;">- Bán các mặt hàng máy tính, PC, Laptop cho các đại lý, Khách hàng mua số lượng lớn.</span><br><span data-mce-mark="1" style="background-image: none; background-position: 0% 0%; background-size: initial; background-repeat: repeat; background-attachment: scroll; background-origin: initial; background-clip: initial;">- Thực hiện quy trình bán hàng của Bộ phận, đạt doanh số được giao.</span><br><span data-mce-mark="1" style="background-image: none; background-position: 0% 0%; background-size: initial; background-repeat: repeat; background-attachment: scroll; background-origin: initial; background-clip: initial;">- Công việc trao đổi khi phỏng vấn.</span></span></p></td><td valign="top" width="264" style="padding: 2.75pt 2.75pt 2.75pt 2.7pt; width: 2.75in;"><div><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;">- Nam, tuổi >23, giao tiếp tốt, ngoại hình khá.</span></div><div><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;">-Tốt nghiệp trung cấp trở lên chuyên ngành Kinh tế, QTKD, Công nghệ, bách khoa..</span></div><div><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;">- Có kinh nghiệm ít nhất 1 năm trong lĩnh vực kinh doanh phân phối Sản Phẩm máy tính. </span></div><div><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;">- Ưu tiên có kiến thức cơ bản về Sản phẩm IT.</span></div></td></tr><tr><td valign="top" width="108" style="padding: 2.75pt 2.75pt 2.75pt 2.7pt; width: 81pt;"><p class="TableContents" align="center" style="text-align: center;"><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;"><strong>Nhân viên thiết kế</strong></span></p><p class="TableContents" align="center" style="text-align: center;"><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;"><strong>Số lượng: 01</strong></span></p></td><td valign="top" width="294" style="padding: 2.75pt 2.75pt 2.75pt 2.7pt; width: 220.45pt;"><p><span data-mce-mark="1" style="color: rgb(0, 0, 0); font-size: medium; font-family: arial, helvetica, sans-serif;"><span data-mce-mark="1" style="color: rgb(54, 54, 54);">-</span> Thiết kế các ấn phẩm cho cty<br>- Chỉnh sửa ảnh, banner quảng cáo chuyên nghiệp cho công ty và đối tác.<br>- Quản lý mảng hình ảnh và đồ họa nói chung của website công ty</span></p><p><span data-mce-mark="1" style="color: rgb(0, 0, 0); font-size: medium; font-family: arial, helvetica, sans-serif;">- Làm Full time hoặc Part time</span></p></td><td valign="top" width="264" style="padding: 2.75pt 2.75pt 2.75pt 2.7pt; width: 2.75in;"><div><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;">- Tốt nghiệp đại học chuyên ngành đồ họa<br>- Có thể thiết kế và sử dụng tốt các phần mềm thiết kế đồ họa như Photoshop, InDesign, Illustrator, Flash, Corel.<br>- Có kinh nghiệm trong thiết kế đồ họa và thiết kế website<br>- Kỹ năng trong nhiếp ảnh và thiết kế hình ảnh 3D là lợi thế<br>- Có khả năng trình bày ý tưởng thông qua các thiết kế.</span></div><div><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;">- Sáng tạo, có thẩm mỹ cao, có cái nhìn tinh tế trong mỹ thuật về màu sắc, không gian.<br>- Có khả năng làm việc theo nhóm và làm việc độc lập<br>- Công việc đòi hỏi tính sáng tạo cao, có nhiều ý tưởng mới,hay và đặc biệt.<br>- Có kỹ năng và thẩm mỹ trong thiết kế website, banner, tờ rơi<span data-mce-mark="1" style="color: rgb(54, 54, 54);"><br>-</span> có khả năng sử dụng thành thạo các công cụ thiết kế (như vẽ tay và các phần mềm thiết kế đồ họa) để đưa những ý tưởng sáng tạo vào ứng dụng thực tiễn.<br>- Làm việc trong môi trường có tính sáng tạo cao</span></div></td></tr><tr><td valign="top" width="108" style="padding: 2.75pt 2.75pt 2.75pt 2.7pt; width: 81pt;"><p class="TableContents" align="center" style="text-align: center;"> </p><p class="TableContents" align="center"><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;"><strong>Nhân viên kinh doanh bán lẻ tại 131 Lê Thanh Nghị</strong></span></p><p class="TableContents" align="center" style="text-align: center;"> </p><p class="TableContents" align="center"><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;"><strong>Số lượng: 01</strong></span></p><p class="TableContents" align="center" style="text-align: center;"> </p></td><td valign="top" width="294" style="padding: 2.75pt 2.75pt 2.75pt 2.7pt; width: 220.45pt;"><span style="font-size: medium; font-family: arial, helvetica, sans-serif;"> - Tiếp đón khách hàng tại Showroom hoặc qua email, điện thoại.<br>- Tư vấn cho khách hàng thông tin về sản phẩm, các chính sách bán hàng và chương trình khuyến mại đang áp dụng.<br>- Hướng dẫn khách hàng thực hiện thủ tục thanh toán và nhận hàng.<br>- Thực hiện công việc khác theo sự phân công trực tiếp Trưởng bộ phận</span></td><td valign="top" width="264" style="padding: 2.75pt 2.75pt 2.75pt 2.7pt; width: 2.75in;"><span style="font-size: medium; font-family: arial, helvetica, sans-serif;"> - Nam, tuổi từ 20 đến 25, ngoại hình khá, giao tiếp tốt.</span><p><span style="font-size: medium; font-family: arial, helvetica, sans-serif;">- Nam có kinh nghiệm bán hàng trong lĩnh vực bán lẻ IT là một lợi thế.<br>- Tốt nghiệp trung cấp trở lên chuyên ngành: Tin học - CNTT; QTKD.<br>- Có kiến thức về các sản phẩm Công nghệ thông tin, Computer.</span></p><p><span style="font-size: medium; font-family: arial, helvetica, sans-serif;">- Yêu thích kinh doanh, khả năng giao tiếp hiệu quả.</span></p><p><span style="font-size: medium; font-family: arial, helvetica, sans-serif;">- Chịu được áp lực công việc.</span></p></td></tr></tbody></table><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;"><strong><span data-mce-mark="1" style="color: red;">QUYỀN LỢI:</span></strong></span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;"><br><span data-mce-mark="1" style="background-image: none; background-position: 0% 0%; background-size: initial; background-repeat: repeat; background-attachment: scroll; background-origin: initial; background-clip: initial;">- Môi trường làm việc ổn định lâu dài, chuyên nghiệp.</span></span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;"><span data-mce-mark="1" style="background-image: none; background-position: 0% 0%; background-size: initial; background-repeat: repeat; background-attachment: scroll; background-origin: initial; background-clip: initial;">- Chính sách khen thưởng kỉ luật rõ ràng.</span></span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;">-<span data-mce-mark="1" style="color: black; background-image: none; background-position: 0% 0%; background-size: initial; background-repeat: repeat; background-attachment: scroll; background-origin: initial; background-clip: initial;">Các chế độ BHXH, BHYT, BHTN theo Luật định và theo quy định công ty.</span></span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;">- <span data-mce-mark="1" style="background-image: none; background-position: 0% 0%; background-size: initial; background-repeat: repeat; background-attachment: scroll; background-origin: initial; background-clip: initial;">Tháng lương thứ 13 theo kết quả kinh doanh năm.</span></span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;">-Thưởng các ngày Lễ lớn: Tết, 30/4, 2/9....</span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;"><span data-mce-mark="1" style="background-image: none; background-position: 0% 0%; background-size: initial; background-repeat: repeat; background-attachment: scroll; background-origin: initial; background-clip: initial;">- Phụ cấp thâm niên theo năm công tác</span></span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;"><span data-mce-mark="1" style="background-image: none; background-position: 0% 0%; background-size: initial; background-repeat: repeat; background-attachment: scroll; background-origin: initial; background-clip: initial;">- Lương, thưởng, BHXH, phúc lợi theo BLLĐ và theo chính sách ưu đãi nhân sự của Công ty.</span></span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;"><span data-mce-mark="1" style="background-image: none; background-position: 0% 0%; background-size: initial; background-repeat: repeat; background-attachment: scroll; background-origin: initial; background-clip: initial;">- Cơ hội đi học tập, công tác trong nước và nước ngoài.</span></span></p><p class="TextBody" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span class="StrongEmphasis" data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;"><span data-mce-mark="1" style="color: red;">NỘP HỒ SƠ:</span></span></p><p class="TextBody" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;"><span class="StrongEmphasis" data-mce-mark="1"><span data-mce-mark="1" style="color: black;">Các ứng viên quan tâm vui lòng gửi hồ sơ qua mail: </span></span><span class="StrongEmphasis" data-mce-mark="1"><span data-mce-mark="1" style="color: red;">tuyendung@hanoicomputer.com </span></span><span class="StrongEmphasis" data-mce-mark="1"><span data-mce-mark="1" style="color: black;">hoặc </span></span><span class="StrongEmphasis" data-mce-mark="1"><span data-mce-mark="1" style="color: red;">nộp trực tiếp </span></span><span class="StrongEmphasis" data-mce-mark="1"><span data-mce-mark="1" style="color: black;">tại Phòng Hành chính TH - Tầng 4, số 131 Lê Thanh Nghị, Hai Bà Trưng, Hà Nội.</span></span></span></p><p class="TextBody" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span class="StrongEmphasis" data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;">Liên hệ Ms Thúy – Phòng HCTH: <span data-mce-mark="1" style="color: red;">043 628 0886</span>, máy lẻ ( 31) HOẶC <span data-mce-mark="1" style="color: red;">0983881626</span> ( liên hệ giờ hành chính)</span></p><p class="TextBody" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span data-mce-mark="1" style="font-size: medium; font-family: arial, helvetica, sans-serif;"><span class="StrongEmphasis" data-mce-mark="1">Trân trọng !</span><br></span></p>', 'Tuyển dụng', '2017-04-04', '2017-04-15');
INSERT INTO `news` (`id`, `title`, `titlekd`, `img`, `content`, `sum`, `created_at`, `updated_at`) VALUES
(7, 'Quy định chung', 'quy-dinh-chung', 'lG5k_baner.png', '<p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px; margin: 0in 0in 0.0001pt;"><strong><span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;">I. Chính sách chung</span></strong></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px; margin: 0in 0in 0.0001pt;"><strong><span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;">1. Chấp thuận các Điều kiện Sử dụng</span></strong></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px; margin: 0in 0in 0.0001pt;"><span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;">Khi sử dụng Website của&nbsp;<span style="font-family: arial, helvetica, sans-serif; font-size: medium; color: rgb(0, 0, 0);"><strong><strong>HANOICOMPUTE</strong>R</strong></span>&nbsp;(<a href="http://hanoicomputer.vn/" target="_blank" style="font-size: initial; cursor: pointer;">HNC</a>), Quý khách đã mặc nhiên chấp thuận các điều khoản và điều kiện sử dụng được quy định dưới đây. Để biết được các sửa đổi mới nhất, Quý khách nên thường xuyên kiểm tra lại “Điều kiện Sử dụng”.&nbsp;<span style="font-family: arial, helvetica, sans-serif; font-size: medium; color: rgb(0, 0, 0);"><strong><strong>HANOICOMPUTE</strong>R</strong></span>&nbsp;có quyền thay đổi, điều chỉnh, thêm hay bớt các nội dung của “Điều kiện Sử dụng” tại bất kỳ thời điểm nào. Nếu Quý khách vẫn tiếp tục sử dụng Website sau khi có các thay đổi như vậy thì có nghĩa là Quý khách đã chấp thuận các thay đổi đó.</span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px; margin: 0in 0in 0.0001pt;"><span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;">&nbsp;</span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px; margin: 0in 0in 0.0001pt;"><strong><span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;">2. Tính chất của thông tin hiển thị</span></strong></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px; margin: 0in 0in 0.0001pt;"><span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;">Các nội dung hiển thị trên Website nhằm mục đích cung cấp thông tin về&nbsp;<span style="font-family: arial, helvetica, sans-serif; font-size: medium; color: rgb(0, 0, 0);"><strong><strong>HANOICOMPUTE</strong>R</strong></span>, về thông tin sản phẩm và dịch vụ mà mà&nbsp;<span style="font-family: arial, helvetica, sans-serif; font-size: medium; color: rgb(0, 0, 0);"><strong><strong>HANOICOMPUTE</strong>R</strong></span>&nbsp;đang cung cấp. Các thông tin khác liên quan nhằm phục vụ nhu cầu tìm hiểu của khách hàng đều được ghi rõ nguồn cung cấp.</span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px; margin: 0in 0in 0.0001pt;"><strong><span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;"><a href="https://www.hanoicomputer.vn/bao-mat-thong-tin-khach-hang/a563.html" target="_blank" style="font-size: initial; cursor: pointer;">3. Chính sách về bảo mật thông tin</a></span></strong></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><strong><span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;">4. Liên kết đến Website khác</span></strong></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;">Website cung cấp một số liên kết tới trang Web hoặc nguồn dữ liệu khác. Quý khách tự chịu trách nhiệm khi sử dụng các liên kết này.&nbsp;<span style="font-family: arial, helvetica, sans-serif; font-size: medium; color: rgb(0, 0, 0);"><strong><strong>HANOICOMPUTE</strong>R</strong></span>&nbsp;không tiến hành thẩm định hay xác thực nội dung, tính chính xác, quan điểm thể hiện tại các trang Web và nguồn dữ liệu liên kết này.&nbsp;<span style="font-family: arial, helvetica, sans-serif; font-size: medium; color: rgb(0, 0, 0);">HANOICOMPUTER</span>&nbsp;từ chối bất cứ trách nhiệm pháp lý nào liên quan tới tính chính xác, nội dung thể hiện, mức độ an toàn và việc cho hiển thị hay che đi các thông tin trên các trang Web và nguồn dữ liệu nói trên.</span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><strong><span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;">5. Liên kết từ Website khác</span></strong></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;"><span style="font-size: 13pt;"><span style="font-family: arial, helvetica, sans-serif; font-size: medium; color: rgb(0, 0, 0);">HANOICOMPUTER</span></span>&nbsp;không cho phép bất kỳ nhà cung cấp dịch vụ internet nào được phép “đặt toàn bộ” hay “nhúng” bất kỳ thành tố nào của Website này sang một trang khác hoặc sử dụng các kỹ thuật làm thay đổi giao diện / hiển thị mặc định của Website mà chưa có sự đồng ý của&nbsp;<span style="font-size: 13pt;"><span style="font-family: arial, helvetica, sans-serif; font-size: medium; color: rgb(0, 0, 0);">HANOICOMPUTER</span></span><br></span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><strong><span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;">6. Miễn trừ trách nhiệm</span></strong></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><strong><span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;">&nbsp;</span></strong><span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;">Thông tin hiển thị tại website này không đi kèm bất kỳ đảm bảo hay cam kết trách nhiệm từ phía&nbsp;<span style="font-size: 13pt;"><span style="font-family: arial, helvetica, sans-serif; font-size: medium; color: rgb(0, 0, 0);">HANOICOMPUTER</span></span>&nbsp;về sự phù hợp của sản phẩm, dịch vụ mà người mua đã lựa chọn</span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;"><span style="font-size: 13pt;"><span style="font-family: arial, helvetica, sans-serif; font-size: medium; color: rgb(0, 0, 0);">HANOICOMPUTER</span></span>&nbsp;từ chối trách nhiệm hay đưa ra đảm bảo rằng website sẽ không có lỗi vận hành, an toàn, không bị gián đoạn hay tính chính xác, đầy đủ và đúng hạn của các thông tin hiển thị</span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;">Khi truy cập vào website này, quý khách mặc nhiên đồng ý rằng&nbsp;<span style="font-size: 13pt;"><span style="font-family: arial, helvetica, sans-serif; font-size: medium; color: rgb(0, 0, 0);">HANOICOMPUTER</span></span>, các nhà cung cấp khác cùng với đối tác liên kết, nhân viên của họ không chịu bất cứ trách nhiệm nào liên quan đến thương tật, mất mát, khiếu kiện, thiệt hại trực tiếp hoặc gián tiếp do không lường trước hoặc do hậu quả để lại dưới bất cứ hình thức nào phát sinh từ việc:</span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;">(1) Sử dụng các thông tin trên website này; (2) các truy cập kết nối từ website này; (3) Đăng ký thành viên, đăng ký nhận thư điện tử hay tham gia vào chương trình khách hàng thường xuyên của&nbsp;<span style="font-size: 13pt;"><span style="font-family: arial, helvetica, sans-serif; font-size: medium; color: rgb(0, 0, 0);">HANOICOMPUTER</span></span><br></span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;">Các điều kiện và hạn chế nêu trên chỉ có hiệu lực trong khuôn khổ pháp luật hiện hành.</span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><strong><span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;">7. Quyền sở hữu trí tuệ</span></strong></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;">Website này và mọi nội dung xếp đặt, hiển thị đều thuộc sở hữu và là tài sản độc quyền khai thác của&nbsp;<span style="font-size: 13pt;"><span style="font-family: arial, helvetica, sans-serif; font-size: medium; color: rgb(0, 0, 0);">HANOICOMPUTER</span></span>và các nhà cung cấp có liên quan khác. Mọi sử dụng, trích dẫn phải không gây thiệt hại cho&nbsp;<span style="font-size: 13pt;"><span style="font-family: arial, helvetica, sans-serif; font-size: medium; color: rgb(0, 0, 0);">HANOICOMPUTER</span></span>&nbsp;và đều phải tuân thủ các điều kiện sau: (1) Chỉ sử dụng cho mục đích cá nhân, phi thương mại; (2) các sao chép hoặc trích dẫn đều phải giữ nguyên dấu hiệu bản quyền hoặc các yết thị về quyền sở hữu trí tuệ như đã thể hiện trong phiên bản gốc; và Tất cả các nội dung được cung cấp tại Website này không được phép nhân bản, hiển thị, công bố, phổ biến, đưa tin tức hay lưu hành cho bất cứ ai, dưới bất kỳ hình thức nào, kể cả trên các Website độc lập khác mà không được sự chấp thuận của<span style="font-size: 13pt;"><span style="font-family: arial, helvetica, sans-serif; font-size: medium; color: rgb(0, 0, 0);">HANOICOMPUTER</span></span>.</span></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><strong><span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;">8. Điều chỉnh và sửa đổi</span></strong></p><p style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><span style="font-size: 13pt; font-family: &quot;Open Sans&quot;, sans-serif;"><span style="font-size: 13pt;"><span style="font-family: arial, helvetica, sans-serif; font-size: medium; color: rgb(0, 0, 0);">HANOICOMPUTER</span></span>&nbsp;bảo lưu quyền thay đổi, chỉnh sửa hoặc chấm dứt hoạt động của Website này vào bất cứ thời điểm nào.</span></p>', 'Quy định chung', '2017-04-04', '2017-04-15'),
(8, 'Khuyến mãi', 'khuyen-mai', '', '<div class="content_detail" style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;"><div><span style="font-size: medium;"><strong>1. Thời gian khuyến mại:</strong> Từ 18.03.2017 đến khi có thông báo mới hoặc đến khi hết quà</span></div><div><span style="font-size: medium;"><strong>2. Đối tượng áp dụng:</strong> Áp dụng cho toàn bộ khách hàng mang case máy tính (kể cả case không mua tại HANOICOMPUTER) đến hệ thống showroom của HANOICOMPUTER để vệ sinh miễn phí</span></div><div><span style="font-size: medium;"><strong>3. Địa điểm:</strong> Áp dụng trên toàn bộ hệ thống showroom của HANOICOMPUTER</span></div><div><span style="font-size: medium;"><strong>4. Nội dung chương trình</strong>:</span></div><div><span style="font-size: medium;">Tất cả khách hàng mang case máy tính tính sử dụng CPU Socket 775 hoặc 115X đến hệ thống showroom của HANOICOMPUTER sẽ được thay quạt tản nhiệt CPU mới Segotep Frost Castle trị giá 199.000đ (FANC357) với giá 0đ và được vệ sinh máy tính miễn phí</span></div><div><span style="font-size: medium;"> <br></span></div><div><span data-mce-mark="1" style="font-size: medium;"><strong>Lưu ý:</strong></span></div><div><span style="font-size: medium;">- Số lượng quà có hạn nên ưu tiên khách hàng đến sớm</span></div><div><span style="font-size: medium;">- Khách hàng đến vệ sinh máy tính tại phòng kỹ thuật.</span></div><div><span style="font-size: medium;">- Chỉ áp dụng vệ sinh cho máy tính sử dụng CPU Socket 775 hoặc 115X</span></div></div><div style="color: rgb(34, 34, 34); font-family: Arial, sans-serif; font-size: 12px;">Chăm sóc khách hàng: 04.36280886/ máy lẻ 0 hoặc Hotline: 09.78.58.00.88 <br>Bán hàng online:  (04) 36280886 máy lẻ 1 </div>', '', '2017-04-15', '2017-04-15');

